let btnOpenList=document.querySelectorAll(".btn__open");btnOpenList.forEach(function(t){t.addEventListener("click",function(){let e=t.closest(".box__questions"),s=e.querySelector(".title__inside-questions"),i=e.querySelector(".text__questions"),n=e.querySelector(".btn__open");s.classList.toggle("title__inside-questions-active"),i.classList.toggle("text__questions-active"),n.classList.toggle("btn__open-active"),e.classList.toggle("box__questions-active")})});